select o.hgvsg, o.variant_class, o.symbol, o.consequence, o.vep_impact, c.interpretations, o.gnomad_af, o.quality, o.zygosity from occurrences o left join clinvar c on o.chromosome=c.chromosome and o.start=c.start and o.reference=c.reference and o.alternate=c.alternate where o.sample_id = %s limit 20;